{
  "name": "etribe",
  "owner": {
    "name": "WishDev",
    "email": "wishdev@example.com"
  },
  "metadata": {
    "description": "Claude Code를 위한 MCP 플러그인, 스킬, 명령어, 에이전트",
    "version": "1.0.0"
  },
  "plugins": [
    {
      "name": "etribe",
      "source": "./",
      "description": "etribe 전체 툴킷: MCP 서버, 명령어, 스킬, 에이전트",
      "version": "1.0.0",
      "mcpServers": {
        "sequential-thinking": {
          "command": "npx",
          "args": ["-y", "@modelcontextprotocol/server-sequential-thinking"]
        },
        "context7": {
          "command": "npx",
          "args": ["-y", "@upstash/context7-mcp"]
        },
        "serena": {
          "command": "uvx",
          "args": ["--from", "git+https://github.com/oraios/serena", "serena", "start-mcp-server"]
        },
        "playwright": {
          "command": "npx",
          "args": ["@playwright/mcp@latest"]
        }
      },
      "commands": [
        "./commands/implement.md",
        "./commands/build.md",
        "./commands/test.md"
      ],
      "skills": [
        "./skills/confidence-check"
      ],
      "agents": [
        "./agents/self-review.md",
        "./agents/root-cause-analyst.md",
        "./agents/refactoring-expert.md"
      ]
    },
    {
      "name": "sequential-thinking",
      "source": "./plugins/sequential-thinking",
      "description": "단계별 사고를 통한 복잡한 문제 해결",
      "version": "1.0.0",
      "mcpServers": {
        "sequential-thinking": {
          "command": "npx",
          "args": ["-y", "@modelcontextprotocol/server-sequential-thinking"]
        }
      }
    },
    {
      "name": "context7",
      "source": "./plugins/context7",
      "description": "라이브러리 문서 및 코드 예제 검색",
      "version": "1.0.0",
      "mcpServers": {
        "context7": {
          "command": "npx",
          "args": ["-y", "@upstash/context7-mcp"]
        }
      }
    },
    {
      "name": "serena",
      "source": "./plugins/serena",
      "description": "시맨틱 코드 분석 및 편집 (LSP 통합)",
      "version": "1.0.0",
      "mcpServers": {
        "serena": {
          "command": "uvx",
          "args": ["--from", "git+https://github.com/oraios/serena", "serena", "start-mcp-server"]
        }
      }
    },
    {
      "name": "playwright",
      "source": "./plugins/playwright",
      "description": "브라우저 자동화 (Microsoft 공식)",
      "version": "1.0.0",
      "mcpServers": {
        "playwright": {
          "command": "npx",
          "args": ["@playwright/mcp@latest"]
        }
      }
    }
  ]
}
