files.plugins/yux-linear-workflow/.claude-plug:
{
  "name": "yux-linear-workflow",
  "version": "1.0.0",
  "description": "Complete Linear workflow integration for Claude Code. Automates issue tracking, branch management, commits, PR creation, CI/CD monitoring, and merging.",
  "author": {
    "name": "wuyuxiangX",
    "email": "wuyuxiang@example.com"
  },
  "homepage": "https://github.com/wuyuxiangX/linear-workflow",
  "license": "MIT",
  "keywords": ["linear", "workflow", "ci-cd", "git", "issue-tracking", "automation"]
}

{
  "$schema": "https://raw.githubusercontent.com/anthropics/claude-code/main/schema/hooks.json",
  "hooks": {
    "UserPromptSubmit": [
      {
        "matcher": "",
        "hooks": [
          {
            "type": "command",
            "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/verify_linear_task.py",
            "timeout": 5
          },
          {
            "type": "prompt",
            "prompt": "Check if Linear workflow is relevant:\n1. If user mentions task/issue/feature work, remind about /yux-linear-start\n2. If on a Linear branch (LIN-xxx), acknowledge the context\n3. If discussing PR/merge, remind about /yux-linear-pr or /yux-linear-merge\nOnly add reminders if truly relevant.",
            "timeout": 5
          }
        ]
      }
    ],
    "PreToolUse": [
      {
        "matcher": "Write|Edit",
        "hooks": [
          {
            "type": "command",
            "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/check_branch.py",
            "timeout": 5
          }
        ]
      },
      {
        "matcher": "Bash",
        "hooks": [
          {
            "type": "command",
            "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/validate_commit.py",
            "timeout": 5
          }
        ]
      }
    ],
    "PreCompact": [
      {
        "matcher": "",
        "hooks": [
          {
            "type": "command",
            "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/sync_progress.py",
            "timeout": 10
          },
          {
            "type": "prompt",
            "prompt": "If the sync_progress script returned an issue_id, consider posting a progress summary to Linear using mcp__linear__create_comment before context compaction.",
            "timeout": 10
          }
        ]
      }
    ],
    "PostToolUse": [
      {
        "matcher": "Bash",
        "hooks": [
          {
            "type": "command",
            "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/post_command.py",
            "timeout": 5
          }
        ]
      }
    ]
  }
}

