  },
  "metadata": {
    "description": "A curated marketplace of Claude Code plugins and skills for enhanced development workflows",
    "version": "0.5.4",
    "pluginRoot": "./plugins"
  },
  "plugins": [
    {
      "name": "devloop",
      "source": "./plugins/devloop",
      "description": "Lightweight development workflow with spike/fresh/continue pattern, git workflow integration (branch-per-plan, PR creation, PR feedback), 14 on-demand skills, and 6 specialized agents. Optimized for efficiency with minimal hooks and direct execution.",
      "category": "development",
      "tags": ["feature-development", "workflow", "agents", "git", "pr-workflow", "fresh-start", "context-management", "go", "react", "java", "python"]
    },
    {
      "name": "security",
      "source": "./plugins/security",
      "description": "Comprehensive security plugin providing OWASP ASVS 5.0-aligned audits via /security:audit command with 17 domain-specific auditor agents, plus live security guard hooks for real-time vulnerability detection during code changes",
      "category": "security",
      "tags": ["security", "audit", "owasp", "asvs", "vulnerability", "code-review", "live-guard", "hooks", "devsecops"]
    },
    {
      "name": "ctx",
      "source": "./plugins/ctx",
      "description": "Persistent memory for Claude Code agents. Stores decisions, patterns, and knowledge in a structured graph database across sessions with auto-injection and enforcement.",
      "category": "development",
      "tags": ["memory", "context", "persistence", "knowledge-graph", "sessions"]
    },
    {
      "name": "wsl-clipboard-fix",
      "source": "./plugins/wsl-clipboard-fix",
      "description": "Fixes image pasting in WSL2 by auto-converting BMP clipboard content to PNG. Runs a background clip2png daemon that monitors the Wayland clipboard during Claude Code sessions.",
      "category": "utilities",
      "tags": ["wsl", "wsl2", "clipboard", "image-paste", "wayland", "bmp", "png"]
    }
  ]
}
